addMessage :: Unicode -> Report -> Report
addMessage message report =
    report {reportMessages = 
                case reportMessages report of
                  "" -> message
                  s -> s ++ convert (Wiki [NewLine, convert message])}

clearMessages :: Report -> Report
clearMessages report = report {reportMessages = ""}
-}

